g.script:
var svg = document.currentScript.ownerDocument;
var w = 1920, h = 1080;
var cx = w/2, cy = h/2;
var maxR = 10, minR = 2;
var step = 40;
for (var x = 0; x < w; x += step) {
  for (var y = 0; y < h; y += step) {
    var dx = x - cx, dy = y - cy;
    var dist = Math.sqrt(dx*dx + dy*dy);
    var r = maxR - (maxR-minR) * (dist / Math.sqrt(cx*cx + cy*cy));
    if (r < minR) r = minR;
    var circle = svg.createElementNS("http://www.w3.org/2000/svg", "circle");
    circle.setAttribute("cx", x);
    circle.setAttribute("cy", y);
    circle.setAttribute("r", r);
    circle.setAttribute("fill", "white");
    svg.documentElement.appendChild(circle);
  }
}
